const scanResult = $input.first().json;
const predictions = scanResult.predictions || [];

// Filter to only high-probability stock-outs (> 0.7)
const highRisk = predictions.filter(p => p.stockout_probability > 0.7);

// Sort by probability descending (most urgent first)
highRisk.sort((a, b) => b.stockout_probability - a.stockout_probability);

if (highRisk.length === 0) {
  return [{
    json: {
      has_alerts: false,
      alert_count: 0,
      message: 'No high-risk stock-outs detected in this scan cycle'
    }
  }];
}

// Return each alert as a separate item for the SplitInBatches node
return highRisk.map(pred => ({
  json: {
    has_alerts: true,
    alert_count: highRisk.length,
    store_id: pred.store_id,
    store_name: pred.store_name,
    product_id: pred.product_id,
    product_name: pred.product_name,
    sku_code: pred.sku_code,
    current_stock: pred.current_stock,
    stockout_probability: pred.stockout_probability,
    days_until_stockout: pred.days_until_stockout,
    suggested_reorder_qty: pred.suggested_reorder_qty,
    distributor_id: pred.distributor_id,
    distributor_phone: pred.distributor_phone,
    territory_id: pred.territory_id
  }
}));

const alert = $input.first().json;

const probability = Math.round(alert.stockout_probability * 100);
const urgency = alert.days_until_stockout <= 2 ? 'URGENT' : 'WARNING';

const message = `${urgency === 'URGENT' ? 'ðŸš¨' : 'âš ï¸'} Stock Alert\n\n` +
  `Product: ${alert.product_name}\n` +
  `SKU: ${alert.sku_code}\n` +
  `Store: ${alert.store_name}\n` +
  `Current Stock: ${alert.current_stock} units\n` +
  `Stock-out Risk: ${probability}%\n` +
  `Est. Days Until Out: ${alert.days_until_stockout}\n` +
  `\nSuggested Reorder: ${alert.suggested_reorder_qty} cases\n` +
  `\nPlease arrange replenishment at the earliest.`;

return [{
  json: {
    distributor_phone: alert.distributor_phone,
    distributor_id: alert.distributor_id,
    store_id: alert.store_id,
    product_id: alert.product_id,
    message: message,
    urgency: urgency,
    probability: probability
  }
}];

// Rate limit: wait 500ms between WhatsApp sends
await new Promise(resolve => setTimeout(resolve, 500));
return $input.all();
